mapper.select:
SELECT rh.named_Insured AS namedInsured, UPPER(status.name) AS status FROM risk_header rh, status status WHERE rh.active_flag = 'Y' AND status.id = rh.status_id
AND rh.status_id = #{statusId}
AND created_date >= #{startDate}
AND created_date <= #{endDate}
ORDER BY rh.id

SELECT rh.named_Insured AS namedInsured, UPPER(status.name) AS status FROM risk_header rh, status status WHERE rh.active_flag = 'Y' AND status.id = rh.status_id
AND rh.status_id = #{searchFilter.statusId}
AND created_date >= #{searchFilter.startDate}
AND created_date <= #{searchFilter.endDate}
ORDER BY rh.id
OFFSET #{length}

SELECT COUNT(*) OVER() AS recordsTotal, rh.named_Insured AS namedInsured, UPPER(status.name) AS status FROM risk_header rh, status status WHERE rh.active_flag = 'Y' AND status.id = rh.status_id
AND rh.status_id = #{searchFilter.statusId}
AND created_date >= #{searchFilter.startDate}
AND created_date <= #{searchFilter.endDate}
ORDER BY rh.id
OFFSET #{length}
